    if (animating) return;
    const nextIndex = activeIndex === 0 ? items.length - 1 : activeIndex - 1;
    setActiveIndex(nextIndex);
  };
  const goToIndex = (newIndex) => {
    if (animating) return;
    setActiveIndex(newIndex);
  };

  const items = [
    {
      src: require("assets/img/thaiBookEx1.jpg"),
    },
    {
      src: require("assets/img/thaiBookEx2.jpg"),
    },
    {
      src: require("assets/img/thaiBookEx3.jpg"),
    },
  ];

